import React,{useEffect,useState} from "react";
import Videoplayercontainer from "../Components/Videoplayercontainer";
import Header from "../Components/Header";
import { useLocation } from "react-router-dom";
import axios from "axios";





 export default function Player() {
    const location = useLocation();
    console.log(location);
    const videoId = location.state.video.id;

    const [playingVideosId,setPlayingVideosId] = useState();

    const playThisVideo = () => {
        axios({
          method: "GET",
          url: `https://youtube.googleapis.com/youtube/v3/search?part=snippet&videoId=${videoId}&maxResults=200&key=${API_KEY}`,
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json; charset=UTF-8",
          },
        })
          .then((res) => {
            setPlayingVideosId(res.data.items);
            console.log(res.data.items);
            //   localStorage.setItem("videos", JSON.stringify(res.data.items));
          })
          .catch((error) => {
            console.log(error);
            // alert(error.response.data.error.message);
          });
      };

      useEffect(() => {
        playThisVideo();
      }, []);



  return (
    <>
      <Header />
      <Videoplayercontainer playingVideosId={playingVideosId}/>
    </>
  );
}
