import React, { useState, useEffect } from "react";
import "./index.css";
import { videoPlayerInfo } from "./data";

export default function Mainvideocontainer({ selectedTag }) {
  const [myFilteredData, setMyFilteredData] = useState(videoPlayerInfo);

  const [videos, setVideos] = useState([]);

  const fetchVideos = () => {
    const valueFromLocalStorage = getValueFromLocalStorage("videos");
    if (valueFromLocalStorage !== null) {
      if (valueFromLocalStorage !== 0) {
        setVideos(valueFromLocalStorage);
        return;
      }
    }

    axios({
      method: "GET",
      url: `https://youtube.googleapis.com/youtube/v3/videos?part=snippet&maxResults=50&regionCode=IN&chart=mostPopular&key=${API_KEY}`,
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => {
        // showing permanent value from local storage
        setValueInLocalStorage("videos", res.data.items);
        // showing temporary value
        setVideos(res.data.items);
      })
      .catch((error) => {
        console.log(error);
        alert(error.response.data.message);
      });
  };
  useEffect(() => {
    fetchVideos();
  }, []);


  useEffect(() => {
    const fltData = videoPlayerInfo.filter(
      (item, index) => item?.tag === selectedTag?.toLowerCase()
    );
    if (selectedTag === "all") {
      setMyFilteredData(videoPlayerInfo);
    } else {
      setMyFilteredData(fltData);
    }
  }, [selectedTag]);

  return (
    <div className="mainVideoContainer">
      <hr></hr>
      <div className="videoRow">
        {myFilteredData.map((video, index) => {
          return (
            <div className="videoInfo" key={index}>
              <div className="videoPlayer">
                <img
                  alt={video.Description}
                  // width="280px"
                  // height="160px"
                  src={video.Videoimg}
                />
              </div>
              <div className="channelLogoAndInfo">
                <div className="channelLogo">
                  <img alt="video-logo" src={video.logo} />
                </div>
                <div className="channelInfo">
                  <div className="channelDescription">
                    <p>{video.Description}</p>
                  </div>
                  <div className="channelName">
                    <p>{video.ChannelName}</p>
                  </div>
                  <div className="viewsAndUploadTime">
                    <p className="views">{video.Views}</p>
                    <span> . </span>
                    <p className="uploadTime">{video.Uploadtime}</p>
                  </div>
                  {/* <p style={{ color: "red" }}>{video.tag}</p> */}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
