import React from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";


export default function Videoplayercontainer() {
  const { videoId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  const navigateToPlayThisVideo = (video) => {
    navigate(`/player/${video.id.videoId}`, {
      state: {
        video,
      },
    });
  };

  const [comments, setcomments] = useState([]);
  const fetchComments = () => {
    axios({
      method: "GET",
      url: `https://youtube.googleapis.com/youtube/v3/commentThreads?part=snippet&videoId=${videoId}&key=${API_KEY}`,
    })
      .then((res) => {
        setcomments(res.data.items);
      })
      .catch((err) => {
        console.log("Error..", err);
      });
  };

  useEffect(() => {
    fetchComments();

    console.log(location.state.item.snippet.description);
  }, []);

  return (
    <div className="playingVideo">
               <iframe
               title="Youtube Video Play"
               width="980"
               height="500"
               frameBorder="0"
               allowFullScreen
               allow="autoplay"
               src={`https://www.youtube.com/embed/${videoId}`}
             ></iframe>
    </div>
  );
}
