import React from "react";

export default function Videoplayercontainer({playingVideosId}) {
  const { videoId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  const redirectToVideoPlayer = (video) => {
    navigate(`/player/${video.id.videoId}`, {
      state: {
        video,
      },
    });
  };

  return (
    <div className="playingVideo">
     <iframe
              title="Youtube Video Play"
              width="980"
              height="500"
              frameBorder="0"
              allowFullScreen
              allow="autoplay"
              src={`https://www.youtube.com/embed/${playingVideosId}`}
            ></iframe>
    </div>
  );
}
