import React from "react";
import "./index.css";
import { AiOutlineMenu } from "react-icons/ai";
import { MdKeyboardVoice } from "react-icons/md";
import { RiVideoAddLine } from "react-icons/ri";
import { BsGrid3X3Gap } from "react-icons/bs";
import { BsBell } from "react-icons/bs";
import { IoIosSearch } from "react-icons/io";
import { IconContext } from "react-icons";
import { FaChromecast } from "react-icons/fa";

export default function Header({ toggleSideBar }) {
  const toggle = () => {
    toggleSideBar();
  };

  const fetchVideos = () => {
    const valueFromLocalStorage = getValueFromLocalStorage("videos");
    if (valueFromLocalStorage !== null) {
      if (valueFromLocalStorage !== 0) {
        setVideos(valueFromLocalStorage);
        return;
      }
    }

    axios({
      method: "GET",
      url: `https://youtube.googleapis.com/youtube/v3/videos?part=snippet&maxResults=50&regionCode=IN&chart=mostPopular&key=${API_KEY}`,
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => {
        // showing permanent value from local storage
        setValueInLocalStorage("videos", res.data.items);
        // showing temporary value
        setVideos(res.data.items);
      })
      .catch((error) => {
        console.log(error);
        alert(error.response.data.message);
      });
  }

  return (
    <header>
      {/* Header logo and sideheader menu part with classname logo */}
      <div className="logo">
        {/* Hamburger menu */}
        <IconContext.Provider value={{ size: 20, className: "hamburgerMenu" }}>
          <div onClick={toggle}>
            <AiOutlineMenu />
          </div>
        </IconContext.Provider>
        <img
          className="youtubeLogo"
          alt="youtube"
          src="https://exchange4media.gumlet.io/news-photo/109829-108730-youtube.jpg?format=webp&w=750&dpr=1.0"
        />
      </div>
      {/* searchbar part  */}
      <div className="searchBar">
        <input placeholder="Search" type="text" spellcheck="false" />
        <IconContext.Provider value={{ size: 24, className: "searchClick" }}>
          <div className="searchClickBox" onClick={fetchVideos}>
            <IoIosSearch />
          </div>
        </IconContext.Provider>
        {/* Voice feature  */}
        <IconContext.Provider value={{ size: 20, className: "voiceRecording" }}>
          <div className="voiceIcon">
            <MdKeyboardVoice />
          </div>
        </IconContext.Provider>
      </div>
      <div className="rightHeader">
        <IconContext.Provider value={{ size: 20, className: "addVideo" }}>
          <div>
            <RiVideoAddLine />
          </div>
        </IconContext.Provider>
        <IconContext.Provider value={{ size: 20, className: "dotMenu" }}>
          <div>
            <BsGrid3X3Gap />
          </div>
        </IconContext.Provider>
        <IconContext.Provider value={{ size: 20, className: "chromeCast" }}>
          <div>
            <FaChromecast />
          </div>
        </IconContext.Provider>

        <IconContext.Provider
          value={{ size: 20, className: "notificationBell" }}
        >
          <div>
            <BsBell />
          </div>
        </IconContext.Provider>

        <div className="profilePhoto">
          <img
            alt="profile"
            src="https://yt3.ggpht.com/ytc/AKedOLTYYiBFMYFq25NUYsXulSXsYz0yTLI3HwAI227qWg=s68-c-k-c0x00ffffff-no-rj"
          />
        </div>
      </div>
    </header>
  );
}
