import React, { useEffect, useState } from "react";
import axios from "axios";
import { API_KEY } from "../Header";

export default function Videoplayercontainer({ videoId }) {
  const [comments, setComments] = useState([]);
  const fetchComments = () => {
    axios({
      method: "GET",
      url: `https://youtube.googleapis.com/youtube/v3/commentThreads?part=snippet&videoId=${videoId}&key=${API_KEY}`,
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => {
        setComments(res.data.items);
        console.log(res.data.items);
        //   localStorage.setItem("videos", JSON.stringify(res.data.items));
      })
      .catch((error) => {
        console.log(error);
        // alert(error.response.data.error.message);
      });
  };

  useEffect(() => {
    fetchComments();
  }, []);

  return (
    <>
      <div className="videoPlayerContainer"></div>
      <div className="playingVideo">
        <iframe
          title="Youtube Video Play"
          width="980"
          height="500"
          frameBorder="0"
          allowFullScreen
          allow="autoplay"
          src={`https://www.youtube.com/embed/${videoId}`}
        ></iframe>
      </div>
      <div className="commentsColumn">
        {comments.map((video, index) => {
          return (
            <div key={index}>
              <h6 className="authorDisplayName">
                {video.snippet.topLevelComment.snippet.authorDisplayName}
              </h6>
              <p className="comment">
                {video.snippet.topLevelComment.snippet.textDisplay}
              </p>
            </div>
          );
        })}
      </div>
    </>
  );
}
