import React, { useEffect, useState } from "react";
import axios from "axios";
import { API_KEY } from "../Header";
import { useLocation } from "react-router-dom";

export default function Videoplayercontainer({ videoId }) {
  const location = useLocation();
  console.log(location);

  const [comments, setComments] = useState([]);
  const fetchComments = () => {
    axios({
      method: "GET",
      // url: `https://youtube.googleapis.com/youtube/v3/search?part=snippet&q=${searchQuery}&maxResults=200&key=${API_KEY}`,
      url: `https://youtube.googleapis.com/youtube/v3/commentThreads?part=snippet&videoId=${videoId}&key=${API_KEY}`,
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => {
        setComments(res.data.items);
        console.log(res.data.items);
        //   localStorage.setItem("videos", JSON.stringify(res.data.items));
      })
      .catch((error) => {
        console.log(error);
        // alert(error.response.data.error.message);
      });
  };

  useEffect(() => {
    fetchComments();
  }, []);

  return (
    <>
      {" "}
      <div className="playingVideo">
        <iframe
          title="Youtube Video Play"
          width="980"
          height="500"
          frameBorder="0"
          allowFullScreen
          allow="autoplay"
          src={`https://www.youtube.com/embed/${videoId}`}
        ></iframe>
      </div>
      <div className="videoRow">
        {comments.map((video, index) => {
          return (
            <div key={index}>
              <p>{video.snippet.videoId}</p>
            </div>
          );
        })}
      </div>
    </>
  );
}
