import React, { useState, useEffect } from "react";
import "./index.css";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import { API_KEY } from "../Header";

export default function Mainvideocontainer({ selectedTag }) {
  const [myFilteredData, setMyFilteredData] = useState([]);

  const fetchVideosFromLocalStorage = JSON.parse(
    localStorage.getItem("videos")
  );
  // console.log(fetchVideosFromLocalStorage);
  useEffect(() => {
    // const fltData = videoPlayerInfo.filter(
    //   (item, index) => item?.tag === selectedTag?.toLowerCase()
    // );
    // if (selectedTag === "all") {
    //   setMyFilteredData(videoPlayerInfo);
    // } else {
    //   setMyFilteredData(fltData);
    // }

    setMyFilteredData(fetchVideosFromLocalStorage);
  }, [fetchVideosFromLocalStorage]);

  const navigate = useNavigate();


 

  
    
      const navigateToPlayThisVideo = () => {
        axios({
          method: "GET",
          url: `https://youtube.googleapis.com/youtube/v3/videos?q=${searchQuery}&part=snippet&maxResults=200&regionCode=IN&chart=mostPopular&key=${API_KEY}`,
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json; charset=UTF-8",
          },
        })
          .then((res) => {
            navigate(`/player/${video.id.videoId}`)

            // localStorage.setItem("videos", JSON.stringify(res.data.items));
            console.log(res.data.items);
          })
          .catch((error) => {
            console.log(error);
            // alert(error.response.data.error.message);
          });
      };
    

  

  return (
    <div className="mainVideoContainer">
      <hr></hr>
      <div className="videoRow">
        {myFilteredData?.map((video, index) => {
          return (
            <div className="videoInfo" key={index}>
              <div className="videoPlayer" >
                <img
                  src={video.snippet.thumbnails.medium.url}
                  alt={video.snippet.title}
                  onClick={()=>navigateToPlayThisVideo(video)}
                />
              </div>
              <div className="channelLogoAndInfo">
                <div className="channelLogo">
                <img
                  src={video.snippet.thumbnails.medium.url}
                  alt={video.snippet.channelTitle}
                />
                </div>
                <div className="channelInfo">
                  <div className="channelDescription" >
                    <p onClick={()=>navigateToPlayThisVideo(video)}>{video.snippet.title}</p>
                  </div>
                  <div className="channelName">
                    <p>{video.snippet.channelTitle}</p>
                  </div>
                  <div className="viewsAndUploadTime">
                    {/* <p className="views">{video.statistics.viewCount}</p> */}
                    <span> . </span>
                    <p className="uploadTime">{video.Uploadtime}</p>
                  </div>
                  {/* <p style={{ color: "red" }}>{video.tag}</p> */}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
